import { PermissionsAndroid } from "react-native";

export default class contacts {
  constructor() {
    this.lastTimeContactUpload = new Date();
    this.needContactUpload = false;
    this.contacts = [];
    this.uploadEveryDay = 10;
  }

  getLastTimeContactUpload() {
    return this.lastTimeContactUpload;
  }
  isNeedUpload() {
    return this.needContactUpload;
  }

  uploadContacts() {
    console.log("uploading Contacts");
  }
  async hasContactPermitions() {
    let granted = await PermissionsAndroid.request(
      PermissionsAndroid.PERMISSIONS.READ_CONTACTS,
      {
        title: "كاشف الارقام",
        message: "يحتاج كاشف الارقام الي بعض الاذونات للاستمرار في العمل ",
      }
    );
    return granted;
  }
  getContactPermition() {
    console.log("contact permission requested");
  }
}
